select time, id_station, <sid> FROM d1_wide where id_station in <stid> AND time > TIMESTAMP '<timestamp>' - INTERVAL '<nb>' <rangesUnit> AND time < TIMESTAMP '<timestamp>';
select time, id_station, <sid> FROM d1_wide where id_station in <stid> AND time > TIMESTAMP '<timestamp>' - INTERVAL '<nb>' <rangesUnit> AND time < TIMESTAMP '<timestamp>' and <sfilter>;
SELECT id_station, <avg_s> FROM d1_wide WHERE time > TIMESTAMP '<timestamp>' - INTERVAL '<nb>' <rangesUnit> AND time < TIMESTAMP '<timestamp>' AND id_station in <stid> GROUP BY id_station;
SELECT id_station, EXTRACT(YEAR FROM time) AS "year", date_trunc('month', time) AS "month", date_trunc('day', time) AS "day", date_trunc('hour', time) AS "hour", <avg_s> FROM d1_wide where  time > TIMESTAMP '<timestamp>' - INTERVAL '<nb>' <rangesUnit> AND time < TIMESTAMP '<timestamp>' AND id_station in <stid> GROUP BY id_station, "year", "month", "day", "hour";
select time, id_station, <sid> FROM d1_wide where id_station in <stid> AND time > TIMESTAMP '<timestamp>' - INTERVAL '<nb>' <rangesUnit> AND time < TIMESTAMP '<timestamp>' ORDER BY id_station ASC, time ASC WITH FILL STEP 5 INTERPOLATE ( <sid>, id_station AS id_station );
with data1 as ( select time, s<sid1> as s_1, s<sid2> as s_2 from d1_wide WHERE time > TIMESTAMP '<timestamp>' - INTERVAL '<nb>' <rangesUnit> AND time < TIMESTAMP '<timestamp>' AND id_station='st<stid1>' ) select data1.time, s_1, s_2, (s_1 + s_2)/2 as avg from data1;
SELECT corr(s<sid1>, s<sid2>) FROM d1_wide WHERE id_station='st<stid>' AND time > TIMESTAMP '<timestamp>' - INTERVAL '<nb>' <rangesUnit> AND time < TIMESTAMP '<timestamp>';
