select time, id_station, <sid> FROM d1 where id_station in <stid> AND time > TIMESTAMP '<timestamp>' - INTERVAL '<nb>' <rangesUnit> AND time < TIMESTAMP '<timestamp>';
select time, id_station, <sid> FROM d1 where id_station in <stid> AND time > TIMESTAMP '<timestamp>' - INTERVAL '<nb>' <rangesUnit> AND time < TIMESTAMP '<timestamp>' and <sfilter>;
SELECT id_station, <avg_s> FROM d1 WHERE time > TIMESTAMP '<timestamp>' - INTERVAL '<nb>' <rangesUnit> AND time < TIMESTAMP '<timestamp>' AND id_station in <stid> GROUP BY id_station;
SELECT id_station, EXTRACT(YEAR FROM time) AS "year", date_trunc('month', time) AS "month", date_trunc('DAY', time) AS "day", date_trunc('HOUR', time) AS "hour", <avg_s> FROM d1 where  time > TIMESTAMP '<timestamp>' - INTERVAL '<nb>' <rangesUnit> AND time < TIMESTAMP '<timestamp>' AND id_station in <stid> GROUP BY id_station, "year", "month", "day", "hour";
SELECT time_bucket_gapfill('5 second', time) AS NEWTIME, id_station, <avg_s>, <interpolate_avg> FROM d1 WHERE time < '<timestamp>' AND time > timestamp '<timestamp>' - interval '<nb> <rangesUnit>' AND id_station in <stid> GROUP BY NEWTIME, id_station ORDER BY NEWTIME;
with data1 as ( select time, s<sid1> as s_1 from d1 WHERE time > TIMESTAMP '<timestamp>' - INTERVAL '<nb>' <rangesUnit> AND time < TIMESTAMP '<timestamp>' AND id_station='st<stid1>' ), data2 as ( select time, s<sid2> as s_2 from d1 WHERE time > TIMESTAMP '<timestamp>' - INTERVAL '<nb>' <rangesUnit> AND time < TIMESTAMP '<timestamp>' AND id_station='st<stid2>' ) select data1.time, s_1, s_2, (s_1 + s_2)/2 as avg from data1,data2 where data1.time = data2.time;
SELECT corr(s<sid1>, s<sid2>) FROM d1 WHERE id_station='st<stid>' AND time > TIMESTAMP '<timestamp>' - INTERVAL '<nb>' <rangesUnit> AND time < TIMESTAMP '<timestamp>';